    "7": [{"type": "Couplet 1", "lines": ["Seigneur, dans ta grande bonté,", "Tu m’as montré ce que l’on doit craindre,", "Des sauterelles qui viennent dévorer,", "Le regain, la terre, tout doit s’éteindre.", "Je crie vers Toi, ô Dieu d’amour,", "Comment Jacob, si faible, tiendra le jour ?"], "start": 10.52}, {"type": "Refrain", "lines": ["Tu es notre refuge, notre lumière,", "Dans les tempêtes, Tu es notre prière.", "Pardonne, ô Seigneur, à Ta création,", "Ta grâce nous sauve, notre seule raison."], "start": 41.1}, {"type": "Couplet 2", "lines": ["Puis Tu m’as montré le feu qui ronge,", "Un abîme qui engloutit tout sur son passage,", "Je crie encore, ô Dieu qui songe,", "Arrête ce jugement, donne-nous un visage.", "Je sais que nous sommes si petits,", "Mais en Toi, Seigneur, nous avons la vie."], "start": 59.04}, {"type": "Refrain", "lines": ["Tu es notre refuge, notre lumière,", "Dans les tempêtes, Tu es notre prière.", "Pardonne, ô Seigneur, à Ta création,", "Ta grâce nous sauve, notre seule raison."], "start": 84.44}, {"type": "Couplet 3", "lines": ["Au niveau, Tu te tiens, bien droit,", "Pour mesurer notre cœur, notre foi,", "Les hauts lieux, Tu les abattras,", "Mais dans Ta miséricorde, nous espérons déjà.", "Amos, le berger, a écouté Ta voix,", "Il a porté Ta parole, il a fait le choix."], "start": 102.18}, {"type": "Refrain", "lines": ["Tu es notre refuge, notre lumière,", "Dans les tempêtes, Tu es notre prière.", "Pardonne, ô Seigneur, à Ta création,", "Ta grâce nous sauve, notre seule raison."], "start": 126.56}, {"type": "Pont", "lines": ["Écoute, ô Israël, l'appel du ciel,", "Le temps est venu de revenir vers le réel.", "Ne fuis pas, ne crains pas, l’Éternel est là,", "Il te restaure, il te relèvera."], "start": 142.16}, {"type": "Refrain", "lines": ["Tu es notre refuge, notre lumière,", "Dans les tempêtes, Tu es notre prière.", "Pardonne, ô Seigneur, à Ta création,", "Ta grâce nous sauve, notre seule raison.", "Severe prophetic warning, intense Middle Eastern 75 BPM, minor key, social justice, divine judgment", "\"Seigneur, notre Refuge\""], "start": 158.42}],
    "8": [{"type": "Couplet 1", "lines": ["Seigneur, Tu m'as montré, une corbeille de fruits,", "Le temps est arrivé, pour le peuple, c'est fini.", "Les chants se changent, en gémissements,", "Ta voix résonne, dans le silence du temps."], "start": 39.54}, {"type": "Refrain", "lines": ["Ô Éternel, Tu es juste et Tu es saint,", "Nous venons vers Toi, dans notre chagrin.", "Rappelle-nous, Seigneur, de Ta vérité,", "Que nos cœurs se tournent vers l'éternité."], "start": 52.42}, {"type": "Couplet 2", "lines": ["Écoutez, ô peuples, ne dévorez pas l'indigent,", "Ne ruinez pas les faibles, ne soyez pas méprisant.", "Quand passera la fête, quand finira le repos,", "Vous trahissez les pauvres, pour un peu de chaos."], "start": 70.52}, {"type": "Refrain", "lines": ["Ô Éternel, Tu es juste et Tu es saint,", "Nous venons vers Toi, dans notre chagrin.", "Rappelle-nous, Seigneur, de Ta vérité,", "Que nos cœurs se tournent vers l'éternité."], "start": 90.96}, {"type": "Couplet 3", "lines": ["Le pays tremble, sous le poids de nos fautes,", "Les cris du désespoir, les âmes en déroute.", "À midi, le soleil s'éteindra dans le ciel,", "Les rires se tairont, tout deviendra cruel."], "start": 109.08}, {"type": "Refrain", "lines": ["Ô Éternel, Tu es juste et Tu es saint,", "Nous venons vers Toi, dans notre chagrin.", "Rappelle-nous, Seigneur, de Ta vérité,", "Que nos cœurs se tournent vers l'éternité."], "start": 127.64}, {"type": "Pont", "lines": ["Il y aura famine, non de pain, non d'eau,", "Mais de Ta parole, ô Dieu, notre flambeau.", "Errants dans le monde, nous cherchons Ta voix,", "Rends-nous l'espoir, fais briller notre foi."], "start": 148.82}, {"type": "Refrain", "lines": ["Ô Éternel, Tu es juste et Tu es saint,", "Nous venons vers Toi, dans notre chagrin.", "Rappelle-nous, Seigneur, de Ta vérité,", "Que nos cœurs se tournent vers l'éternité.", "Severe prophetic warning, intense Middle Eastern 75 BPM, minor key, social justice, divine judgment", "\"Corbeille de Fruits : Retour à l'Éternel\""], "start": 166.84}],
    "9": [{"type": "Couplet 1", "lines": ["Ô Seigneur, sur l'autel Tu te tiens,", "Ta voix résonne, ton jugement s'éteint.", "Frappe les chapiteaux, les seuils tremblent,", "Ton courroux, Seigneur, sur nous s'assemble."], "start": 0.0}, {"type": "Refrain", "lines": ["Éternel, Tu es grand, dans les cieux et la terre,", "Ton regard est sur nous, Tu es notre lumière.", "De la mort à la vie, Ta main nous relèvera,", "Dans Ta bonté, Seigneur, nos âmes Te loueront."], "start": 39.92}, {"type": "Couplet 2", "lines": ["Si nous fuyons, où pourrions-nous aller?", "Dans les cieux, ou au fond de la mer cachés,", "Tu es là, Tu cherches, aucun n'échappera,", "Ta justice s'affiche, nul ne se sauvera."], "start": 52.14}, {"type": "Refrain", "lines": ["Éternel, Tu es grand, dans les cieux et la terre,", "Ton regard est sur nous, Tu es notre lumière.", "De la mort à la vie, Ta main nous relèvera,", "Dans Ta bonté, Seigneur, nos âmes Te loueront."], "start": 79.54}, {"type": "Couplet 3", "lines": ["Mais dans Ta miséricorde, une promesse est là,", "La maison de David, Tu relèveras.", "Des ruines, Tu bâtis, un peuple restauré,", "Dans la joie, ensemble, nous allons Te chanter."], "start": 89.2}, {"type": "Refrain", "lines": ["Éternel, Tu es grand, dans les cieux et la terre,", "Ton regard est sur nous, Tu es notre lumière.", "De la mort à la vie, Ta main nous relèvera,", "Dans Ta bonté, Seigneur, nos âmes Te loueront."], "start": 113.93999999999998}, {"type": "Pont", "lines": ["Les jours viennent, ô Seigneur,", "Où la moisson sera grande,", "Nous bâtirons, nous planterons,", "Dans Ta grâce, nous marcherons."], "start": 125.58}, {"type": "Refrain", "lines": ["Éternel, Tu es grand, dans les cieux et la terre,", "Ton regard est sur nous, Tu es notre lumière.", "De la mort à la vie, Ta main nous relèvera,", "Dans Ta bonté, Seigneur, nos âmes Te loueront.", "Severe prophetic warning, intense Middle Eastern 75 BPM, minor key, social justice, divine judgment", "\"Relève-moi, Ô Éternel!\""], "start": 140.88}]
